const item=document.querySelector(".item"),placeholders=document.querySelectorAll(".placeholder");item.addEventListener("dragstart",start),item.addEventListener("dragend",end);for(const e of placeholders)e.addEventListener("dragover",dragover),e.addEventListener("dragenter",dragenter),e.addEventListener("dragleave",dragleave),e.addEventListener("drop",drop);function start(e){e.target.classList.add("hold"),setTimeout(()=>e.target.classList.add("hide"),0)}function end(e){e.target.className="item"}function dragover(e){e.preventDefault()}function dragenter(e){e.target.classList.add("hovered")}function dragleave(e){e.target.classList.remove("hovered")}function drop(e){e.target.classList.remove("hovered"),e.target.append(item)}